const webhookInput = $input.first().json;
const models = $('Model Registry').first().json.models;

let modelName = null;
let formData = null;
let userMessage = null;

if (webhookInput.body) {
  modelName = webhookInput.body.model_name;
  formData = webhookInput.body.form_data;
  userMessage = webhookInput.body.message;
} else {
  modelName = webhookInput.model_name;
  formData = webhookInput.form_data;
  userMessage = webhookInput.message;
}

return [{
  json: {
    model_name: modelName,
    form_data: formData,
    user_message: userMessage,
    models: models
  }
}];

const inputData = $input.first().json;

// Validate input
if (!inputData) {
  return [{
    json: {
      error: true,
      response: 'No input data received',
      type: 'error'
    }
  }];
}

const modelName = inputData.model_name;
const formData = inputData.form_data;
const models = inputData.models;

// Check if model name exists
if (!modelName) {
  return [{
    json: {
      error: true,
      response: 'No model name provided',
      type: 'error'
    }
  }];
}

// Check if models list exists
if (!models || !Array.isArray(models)) {
  return [{
    json: {
      error: true,
      response: 'Models list not found',
      type: 'error'
    }
  }];
}

// Find the model configuration
const modelConfig = models.find(m => 
  m.name.toLowerCase() === modelName.toLowerCase()
);

if (!modelConfig) {
  return [{
    json: {
      error: true,
      response: 'Model not found: ' + modelName,
      type: 'error',
      available_models: models.map(m => m.name)
    }
  }];
}

// Check if form data is provided
if (!formData || Object.keys(formData).length === 0) {
  return [{
    json: {
      type: 'form_required',
      model_name: modelConfig.name,
      form_schema: {
        inputs: modelConfig.inputs,
        input_types: modelConfig.input_types
      },
      response: 'Please provide input data for ' + modelConfig.name
    }
  }];
}

// Form data exists, proceed with prediction
return [{
  json: {
    has_form_data: true,
    model_config: modelConfig,
    form_data: formData
  }
}];

const inputData = $input.first().json;

// Validate input
if (!inputData) {
  return [{
    json: {
      error: true,
      response: 'No input data received'
    }
  }];
}

const modelConfig = inputData.model_config;
const formData = inputData.form_data;

// Check if modelConfig exists
if (!modelConfig) {
  return [{
    json: {
      error: true,
      response: 'Model configuration missing. Please select a model first.'
    }
  }];
}

// Check if formData exists
if (!formData) {
  return [{
    json: {
      error: true,
      response: 'Form data missing. Please fill in the form.'
    }
  }];
}

const cleanedData = {};
const inputs = modelConfig.inputs || [];
const inputTypes = modelConfig.input_types || {};

// Process each input field
inputs.forEach(field => {
  const value = formData[field];
  const expectedType = inputTypes[field];

  if (expectedType === 'number') {
    cleanedData[field] = parseFloat(value) || 0;
  } else if (expectedType === 'boolean') {
    cleanedData[field] = value === true || value === 'true' || value === 'yes' || value === 1;
  } else {
    cleanedData[field] = String(value || '');
  }
});

return [{
  json: {
    url: modelConfig.url,
    method: modelConfig.method,
    data: cleanedData,
    model_name: modelConfig.name
  }
}];

const prediction = $input.first().json;
const requestData = $('Prepare ML Request').first().json;

return [{
  json: {
    type: 'prediction_result',
    response: 'âœ… Prediction completed successfully!',
    prediction: prediction,
    model_used: requestData.model_name,
    success: true
  }
}];

const inputData = $input.first().json;
const userMessage = inputData.user_message;
const models = inputData.models;

const systemMsg = 'You are an ML assistant. Available models: ' + models.map(m => m.name).join(', ') + '. Help users understand which model they need. Be brief and helpful.';

return [{
  json: {
    messages: [
      { role: 'system', content: systemMsg },
      { role: 'user', content: userMessage }
    ]
  }
}];

const aiResponse = $input.first().json;
let content = 'AI response received';

if (aiResponse.choices && aiResponse.choices[0] && aiResponse.choices[0].message) {
  content = aiResponse.choices[0].message.content;
}

return [{
  json: {
    type: 'ai_chat',
    response: content,
    success: true
  }
}];
